mapper.insert:
INSERT INTO t_board
SET title = #{title}
, ctnt = #{ctnt}
, iuser = ${iuser}

mapper.update:
UPDATE t_board
SET title = #{title}
, ctnt = #{ctnt}
, updated_at = current_timestamp()
WHERE iboard = ${iboard} AND iuser = ${iuser}

mapper.delete:
Delete from t_board A
INNER JOIN t_board_cmt B
ON A.iuser = B.iuser
WHERE
        A.iboard = #{iboard}
        A.iuser = #{iuser}
        B.iboardCmt = #{iboardCmt}
